import React from 'react';

const Dashboard = ({ products }) => {
  const lowStockProducts = products.filter(product => product.lowStockAlert);
  const totalProducts = products.length;
  const totalValue = products.reduce((sum, product) => sum + (product.price * product.quantity), 0);

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Total Products</h3>
        <p>{totalProducts}</p>
      </div>
      
      <div className="card">
        <h3>Inventory Value</h3>
        <p>M{totalValue.toFixed(2)}</p>
      </div>
      
      <div className="card">
        <h3>Low Stock Alerts</h3>
        <p>{lowStockProducts.length} products need restocking</p>
      </div>
      
      <div className="card">
        <h3>Low Stock Items</h3>
        <ul className="low-stock-list">
          {lowStockProducts.map(product => (
            <li key={product.id} className="low-stock-item">
              <span>{product.name}</span>
              <span className="low-stock-alert">{product.quantity} left</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
