import React, { useState } from "react";

const StockManagement = ({ products, transactions, refreshProducts, refreshTransactions }) => {
  const [adjustments, setAdjustments] = useState({}); // { productId: { type, quantity } }

  const updateAdjustment = (productId, field, value) => {
    setAdjustments((prev) => ({
      ...prev,
      [productId]: {
        ...prev[productId],
        [field]: value,
      },
    }));
  };

  const handleStockAdjustment = async (e) => {
    e.preventDefault();

    const itemsToAdjust = Object.entries(adjustments).filter(
      ([, adj]) => adj.quantity && adj.quantity > 0
    );

    if (itemsToAdjust.length === 0) {
      alert("Please enter at least one adjustment.");
      return;
    }

    try {
      for (const [productId, adj] of itemsToAdjust) {
        const product = products.find((p) => p.id === parseInt(productId));
        if (!product) continue;

        let newQuantity = product.quantity;
        const qty = parseInt(adj.quantity);

        if (adj.type === "add") {
          newQuantity += qty;
        } else if (adj.type === "remove") {
          newQuantity -= qty;
          if (newQuantity < 0) newQuantity = 0;
        }

        // Update product
        await fetch(`http://localhost:3001/products/${product.id}`, {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ quantity: newQuantity }),
        });

        // Record transaction
        await fetch("http://localhost:3001/transactions", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            productId: product.id,
            type: adj.type === "add" ? "restock" : "adjustment",
            quantity: qty,
            date: new Date().toISOString(),
          }),
        });
      }

      setAdjustments({});
      refreshProducts();
      refreshTransactions();
      alert("Stock adjustments applied successfully!");
    } catch (error) {
      console.error("Error adjusting stock:", error);
      alert("Error adjusting stock. Please try again.");
    }
  };

  return (
    <div>
      <h2>Stock Management</h2>

      <div className="card">
        <h3>Adjust Stock Levels</h3>
        <form onSubmit={handleStockAdjustment}>
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Current Stock</th>
                <th>Adjustment Type</th>
                <th>Quantity</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => {
                const adj = adjustments[product.id] || { type: "add", quantity: "" };
                return (
                  <tr key={product.id}>
                    <td>{product.name}</td>
                    <td>{product.quantity}</td>
                    <td>
                      <select
                        value={adj.type}
                        onChange={(e) =>
                          updateAdjustment(product.id, "type", e.target.value)
                        }
                      >
                        <option value="add">Add Stock</option>
                        <option value="remove">Remove Stock</option>
                      </select>
                    </td>
                    <td>
                      <input
                        type="number"
                        min="0"
                        value={adj.quantity}
                        onChange={(e) =>
                          updateAdjustment(product.id, "quantity", e.target.value)
                        }
                      />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          <button type="submit" className="btn btn-primary" style={{ marginTop: "10px" }}>
            Apply Adjustments
          </button>
        </form>
      </div>

      <div className="card">
        <h3>Stock Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Product</th>
              <th>Type</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => {
              const product = products.find((p) => p.id === transaction.productId);
              return (
                <tr key={transaction.id}>
                  <td>{new Date(transaction.date).toLocaleDateString()}</td>
                  <td>{product ? product.name : "Unknown Product"}</td>
                  <td>{transaction.type}</td>
                  <td>{transaction.quantity}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default StockManagement;
