import React, { useState } from 'react';

const ProductManagement = ({ products, refreshProducts }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    costPrice: '', // Added cost price field
    price: '',
    quantity: '',
    lowStockThreshold: '5'
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const productData = {
      ...formData,
      costPrice: parseFloat(formData.costPrice), // Parse cost price
      price: parseFloat(formData.price),
      quantity: parseInt(formData.quantity),
      lowStockThreshold: parseInt(formData.lowStockThreshold)
    };

    try {
      if (isEditing) {
        // For editing, we need to preserve the ID
        await fetch(`http://localhost:3001/products/${currentProduct.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            ...currentProduct, // Keep the original ID and other properties
            ...productData     // Update with new values
          })
        });
      } else {
        // For new products, let json-server generate the ID
        await fetch('http://localhost:3001/products', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(productData)
        });
      }

      resetForm();
      refreshProducts();
      alert(`Product ${isEditing ? 'updated' : 'added'} successfully!`);
    } catch (error) {
      console.error('Error saving product:', error);
      alert('Error saving product. Please check the console for details.');
    }
  };

  const handleEdit = (product) => {
    setCurrentProduct(product);
    setIsEditing(true);
    setFormData({
      name: product.name,
      description: product.description,
      category: product.category,
      costPrice: product.costPrice ? product.costPrice.toString() : '', // Added cost price
      price: product.price.toString(),
      quantity: product.quantity.toString(),
      lowStockThreshold: product.lowStockThreshold.toString()
    });
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this product? This action cannot be undone.')) {
      try {
        const response = await fetch(`http://localhost:3001/products/${id}`, {
          method: 'DELETE'
        });
        
        if (response.ok) {
          refreshProducts();
          alert('Product deleted successfully!');
        } else {
          alert('Failed to delete product. Please try again.');
        }
      } catch (error) {
        console.error('Error deleting product:', error);
        alert('Error deleting product. Please check the console for details.');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      category: '',
      costPrice: '', // Reset cost price
      price: '',
      quantity: '',
      lowStockThreshold: '5'
    });
    setIsEditing(false);
    setCurrentProduct(null);
  };

  // Calculate profit margin for display
  const calculateProfitMargin = () => {
    if (formData.costPrice && formData.price) {
      const cost = parseFloat(formData.costPrice);
      const price = parseFloat(formData.price);
      if (cost > 0 && price > cost) {
        const profit = price - cost;
        const margin = (profit / price) * 100;
        return `${margin.toFixed(1)}% (M${profit.toFixed(2)} per unit)`;
      }
    }
    return 'N/A';
  };

  return (
    <div>
      <h2>Product Management</h2>
      
      <div className="card">
        <h3>{isEditing ? 'Edit Product' : 'Add New Product'}</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Product Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Category</label>
            <input
              type="text"
              name="category"
              value={formData.category}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Cost Price (M)</label>
            <input
              type="number"
              step="0.01"
              min="0"
              name="costPrice"
              value={formData.costPrice}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Selling Price (M)</label>
            <input
              type="number"
              step="0.01"
              min="0"
              name="price"
              value={formData.price}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Profit Margin</label>
            <div className="profit-margin-display">
              {calculateProfitMargin()}
            </div>
          </div>
          
          <div className="form-group">
            <label>Initial Quantity</label>
            <input
              type="number"
              min="0"
              name="quantity"
              value={formData.quantity}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Low Stock Threshold</label>
            <input
              type="number"
              min="1"
              name="lowStockThreshold"
              value={formData.lowStockThreshold}
              onChange={handleInputChange}
              required
            />
          </div>
          
          <button type="submit" className="btn btn-primary">
            {isEditing ? 'Update Product' : 'Add Product'}
          </button>
          
          {isEditing && (
            <button type="button" className="btn" onClick={resetForm}>
              Cancel
            </button>
          )}
        </form>
      </div>
      
      <div className="card">
        <h3>Product List</h3>
        {products.length === 0 ? (
          <p>No products found. Add your first product above.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Category</th>
                <th>Cost (M)</th>
                <th>Price (M)</th>
                <th>Margin</th>
                <th>Quantity</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => {
                const profitMargin = product.costPrice && product.price > product.costPrice
                  ? `${((product.price - product.costPrice) / product.price * 100).toFixed(1)}%`
                  : 'N/A';
                
                return (
                  <tr key={product.id}>
                    <td>{product.name}</td>
                    <td>{product.category}</td>
                    <td>{product.costPrice ? `M${product.costPrice.toFixed(2)}` : 'N/A'}</td>
                    <td>M{product.price.toFixed(2)}</td>
                    <td>{profitMargin}</td>
                    <td>{product.quantity}</td>
                    <td>
                      {product.lowStockAlert ? (
                        <span className="low-stock-alert">Low Stock</span>
                      ) : (
                        <span>In Stock</span>
                      )}
                    </td>
                    <td className="actions">
                      <button 
                        className="btn btn-primary" 
                        onClick={() => handleEdit(product)}
                      >
                        Edit
                      </button>
                      <button 
                        className="btn btn-danger" 
                        onClick={() => handleDelete(product.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default ProductManagement;
