import React, { useState } from 'react';

const Sales = ({ products, refreshProducts, refreshTransactions }) => {
  const [saleItems, setSaleItems] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [saleQuantity, setSaleQuantity] = useState('');

  const addItemToSale = () => {
    if (!selectedProduct || !saleQuantity) return;
    
    const product = products.find(p => p.id === parseInt(selectedProduct));
    if (!product) return;
    
    const quantity = parseInt(saleQuantity);
    
    // Check if product has sufficient stock
    if (quantity > product.quantity) {
      alert(`Not enough stock. Only ${product.quantity} available.`);
      return;
    }
    
    // Check if product is already in sale items
    const existingItemIndex = saleItems.findIndex(item => item.productId === product.id);
    
    if (existingItemIndex >= 0) {
      // Update existing item quantity
      const updatedItems = [...saleItems];
      const newQuantity = updatedItems[existingItemIndex].quantity + quantity;
      
      // Check if updated quantity exceeds stock
      if (newQuantity > product.quantity) {
        alert(`Cannot add more. Only ${product.quantity - updatedItems[existingItemIndex].quantity} additional available.`);
        return;
      }
      
      updatedItems[existingItemIndex].quantity = newQuantity;
      setSaleItems(updatedItems);
    } else {
      // Add new item to sale
      setSaleItems([...saleItems, {
        productId: product.id,
        name: product.name,
        price: product.price,
        quantity: quantity,
        currentStock: product.quantity // Track current stock
      }]);
    }
    
    setSelectedProduct('');
    setSaleQuantity('');
  };

  const removeItemFromSale = (index) => {
    const updatedItems = [...saleItems];
    updatedItems.splice(index, 1);
    setSaleItems(updatedItems);
  };

  const updateItemQuantity = (index, newQuantity) => {
    if (newQuantity < 1) return;
    
    const updatedItems = [...saleItems];
    const product = products.find(p => p.id === updatedItems[index].productId);
    
    if (newQuantity > product.quantity) {
      alert(`Cannot set quantity to ${newQuantity}. Only ${product.quantity} available.`);
      return;
    }
    
    updatedItems[index].quantity = newQuantity;
    setSaleItems(updatedItems);
  };

  const processSale = async () => {
    if (saleItems.length === 0) return;
    
    try {
      // Update product quantities and record transactions
      for (const item of saleItems) {
        const product = products.find(p => p.id === item.productId);
        const newQuantity = product.quantity - item.quantity;
        
        // Update product quantity
        await fetch(`http://localhost:3001/products/${product.id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ quantity: newQuantity })
        });
        
        // Record transaction
        await fetch('http://localhost:3001/transactions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            productId: product.id,
            type: 'sale',
            quantity: item.quantity,
            unitPrice: product.price,
            totalAmount: item.quantity * product.price,
            date: new Date().toISOString()
          })
        });
      }
      
      // Clear sale items
      setSaleItems([]);
      refreshProducts();
      refreshTransactions();
      alert('Sale processed successfully!');
    } catch (error) {
      console.error('Error processing sale:', error);
      alert('Error processing sale. Please try again.');
    }
  };

  const getSubtotal = () => {
    return saleItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const cancelSale = () => {
    if (saleItems.length > 0 && window.confirm('Are you sure you want to cancel this sale?')) {
      setSaleItems([]);
    }
  };

  return (
    <div>
      <h2>Sales</h2>
      
      <div className="card">
        <h3>New Sale</h3>
        
        <div className="form-group">
          <label>Product</label>
          <select 
            value={selectedProduct} 
            onChange={(e) => setSelectedProduct(e.target.value)}
          >
            <option value="">Select a product</option>
            {products.map(product => (
              <option key={product.id} value={product.id} disabled={product.quantity === 0}>
                {product.name} - M{product.price.toFixed(2)} ({product.quantity} in stock)
                {product.quantity === 0 && ' - OUT OF STOCK'}
              </option>
            ))}
          </select>
        </div>
        
        <div className="form-group">
          <label>Quantity</label>
          <input
            type="number"
            value={saleQuantity}
            onChange={(e) => setSaleQuantity(e.target.value)}
            min="1"
            disabled={!selectedProduct}
          />
        </div>
        
        <button 
          type="button" 
          className="btn btn-primary" 
          onClick={addItemToSale}
          disabled={!selectedProduct || !saleQuantity}
        >
          Add to Sale
        </button>
      </div>
      
      {saleItems.length > 0 && (
        <div className="card">
          <h3>Sale Items</h3>
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Price (M)</th>
                <th>Quantity</th>
                <th>Subtotal (M)</th>
                <th>Stock After Sale</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {saleItems.map((item, index) => {
                const product = products.find(p => p.id === item.productId);
                const stockAfterSale = product ? product.quantity - item.quantity : 0;
                
                return (
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>M{item.price.toFixed(2)}</td>
                    <td>
                      <input
                        type="number"
                        value={item.quantity}
                        onChange={(e) => updateItemQuantity(index, parseInt(e.target.value))}
                        min="1"
                        style={{ width: '60px' }}
                      />
                    </td>
                    <td>M{(item.price * item.quantity).toFixed(2)}</td>
                    <td className={stockAfterSale <= (product.lowStockThreshold || 5) ? 'low-stock-alert' : ''}>
                      {stockAfterSale}
                    </td>
                    <td>
                      <button 
                        className="btn btn-danger" 
                        onClick={() => removeItemFromSale(index)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="2"><strong>Total</strong></td>
                <td><strong>{saleItems.reduce((sum, item) => sum + item.quantity, 0)}</strong></td>
                <td><strong>M{getSubtotal().toFixed(2)}</strong></td>
                <td></td>
                <td>
                  <button 
                    className="btn btn-success" 
                    onClick={processSale}
                  >
                    Process Sale
                  </button>
                  <button 
                    className="btn btn-secondary" 
                    onClick={cancelSale}
                    style={{ marginLeft: '10px' }}
                  >
                    Cancel Sale
                  </button>
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      )}
    </div>
  );
};

export default Sales;
