import React, { useState } from "react";

const Sales = ({ products, setProducts, transactions, setTransactions }) => {
  const [selectedProducts, setSelectedProducts] = useState({}); // { productId: quantity }

  const toggleProduct = (id) => {
    setSelectedProducts((prev) => {
      if (prev[id]) {
        const { [id]: _, ...rest } = prev;
        return rest;
      }
      return { ...prev, [id]: 1 }; // default quantity 1
    });
  };

  const updateQuantity = (id, qty) => {
    const product = products.find((p) => p.id === id);
    if (!product) return;
    if (qty > product.quantity) {
      alert(`Only ${product.quantity} available for ${product.name}`);
      return;
    }
    setSelectedProducts((prev) => ({ ...prev, [id]: qty }));
  };

  const processSale = async () => {
    const items = Object.entries(selectedProducts)
      .filter(([_, qty]) => qty > 0)
      .map(([id, qty]) => {
        const product = products.find((p) => p.id === parseInt(id));
        return { product, quantity: qty };
      });

    if (items.length === 0) {
      alert("Select at least one product with quantity.");
      return;
    }

    try {
      const newTransactions = [];

      for (const item of items) {
        const newStock = item.product.quantity - item.quantity;

        // Update product stock
        const res = await fetch(`http://localhost:3001/products/${item.product.id}`, {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ quantity: newStock }),
        });
        const updatedProduct = await res.json();

        // Record transaction
        const transRes = await fetch("http://localhost:3001/transactions", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            productId: item.product.id,
            type: "sale",
            quantity: item.quantity,
            unitPrice: item.product.price,
            totalAmount: item.product.price * item.quantity,
            date: new Date().toISOString(),
          }),
        });
        const newTransaction = await transRes.json();
        newTransactions.push(newTransaction);

        setProducts((prev) =>
          prev.map((p) => (p.id === updatedProduct.id ? updatedProduct : p))
        );
      }

      setTransactions((prev) => [...prev, ...newTransactions]);
      setSelectedProducts({});
      alert("Sale processed successfully!");
    } catch (error) {
      console.error("Error processing sale:", error);
      alert("Error processing sale. Check your server.");
    }
  };

  const getSubtotal = () =>
    Object.entries(selectedProducts).reduce((sum, [id, qty]) => {
      const product = products.find((p) => p.id === parseInt(id));
      return sum + (product?.price || 0) * qty;
    }, 0);

  return (
    <div>
      <h2>Sales</h2>

      <div className="card">
        <h3>Select Products</h3>
        <table>
          <thead>
            <tr>
              <th>Select</th>
              <th>Product</th>
              <th>Price (M)</th>
              <th>Stock</th>
              <th>Quantity</th>
              <th>Subtotal (M)</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => {
              const qty = selectedProducts[product.id] || 0;
              return (
                <tr key={product.id}>
                  <td>
                    <input
                      type="checkbox"
                      checked={!!selectedProducts[product.id]}
                      disabled={product.quantity === 0}
                      onChange={() => toggleProduct(product.id)}
                    />
                  </td>
                  <td>{product.name}</td>
                  <td>M{product.price.toFixed(2)}</td>
                  <td>{product.quantity}</td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      max={product.quantity}
                      disabled={!selectedProducts[product.id]}
                      value={qty}
                      onChange={(e) =>
                        updateQuantity(product.id, parseInt(e.target.value) || 0)
                      }
                      style={{ width: "60px" }}
                    />
                  </td>
                  <td>{qty > 0 ? "M" + (qty * product.price).toFixed(2) : "-"}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {Object.keys(selectedProducts).length > 0 && (
        <div className="card">
          <h3>Sale Summary</h3>
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(selectedProducts)
                .filter(([_, qty]) => qty > 0)
                .map(([id, qty]) => {
                  const product = products.find((p) => p.id === parseInt(id));
                  return (
                    <tr key={id}>
                      <td>{product.name}</td>
                      <td>M{product.price.toFixed(2)}</td>
                      <td>{qty}</td>
                      <td>M{(qty * product.price).toFixed(2)}</td>
                    </tr>
                  );
                })}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="3"><strong>Total</strong></td>
                <td><strong>M{getSubtotal().toFixed(2)}</strong></td>
              </tr>
            </tfoot>
          </table>
          <button className="btn btn-success" onClick={processSale}>
            Process Sale
          </button>
          <button
            className="btn btn-secondary"
            style={{ marginLeft: "10px" }}
            onClick={() => setSelectedProducts({})}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default Sales;
