import React, { useState } from "react";

const Sales = ({ products, setProducts, transactions, setTransactions }) => {
  const [selectedProducts, setSelectedProducts] = useState({}); // { productId: quantity }

  // Toggle selection of a product
  const toggleSelection = (productId) => {
    setSelectedProducts((prev) => {
      if (prev[productId]) {
        const { [productId]: _, ...rest } = prev;
        return rest; // remove if already selected
      } else {
        return { ...prev, [productId]: 1 }; // default quantity 1
      }
    });
  };

  // Update quantity for a selected product
  const updateQuantity = (productId, qty) => {
    if (qty < 0 || isNaN(qty)) return;
    const product = products.find((p) => p.id === productId);
    if (!product) return;

    if (qty > product.quantity) {
      alert(`Only ${product.quantity} units of ${product.name} are available.`);
      return;
    }

    setSelectedProducts((prev) => ({ ...prev, [productId]: qty }));
  };

  // Process all selected products in one sale
  const processSale = async () => {
    const itemsToSell = Object.entries(selectedProducts)
      .filter(([_, qty]) => qty > 0)
      .map(([id, qty]) => {
        const product = products.find((p) => p.id === parseInt(id));
        return { product, quantity: qty };
      });

    if (itemsToSell.length === 0) {
      alert("Please select at least one product and enter a valid quantity.");
      return;
    }

    try {
      const updatedProducts = [...products];
      const newTransactions = [];

      for (const item of itemsToSell) {
        const newStock = item.product.quantity - item.quantity;

        // Update product on server
        await fetch(`http://localhost:3001/products/${item.product.id}`, {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ quantity: newStock }),
        });

        // Record transaction on server
        await fetch("http://localhost:3001/transactions", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            productId: item.product.id,
            type: "sale",
            quantity: item.quantity,
            unitPrice: item.product.price,
            totalAmount: item.product.price * item.quantity,
            date: new Date().toISOString(),
          }),
        });

        // Update local products array
        const index = updatedProducts.findIndex((p) => p.id === item.product.id);
        if (index !== -1) updatedProducts[index].quantity = newStock;

        // Push transaction locally with temporary id
        newTransactions.push({
          id: Date.now() + Math.random(),
          productId: item.product.id,
          type: "sale",
          quantity: item.quantity,
          unitPrice: item.product.price,
          totalAmount: item.product.price * item.quantity,
          date: new Date().toISOString(),
        });
      }

      // Update state once
      setProducts(updatedProducts);
      setTransactions((prev) => [...prev, ...newTransactions]);
      setSelectedProducts({});
      alert("Sale processed successfully!");
    } catch (error) {
      console.error("Error processing sale:", error);
      alert("Error processing sale. Please try again.");
    }
  };

  const getSubtotal = () =>
    Object.entries(selectedProducts).reduce((sum, [id, qty]) => {
      const product = products.find((p) => p.id === parseInt(id));
      return sum + (product?.price || 0) * qty;
    }, 0);

  return (
    <div>
      <h2>Sales</h2>

      {/* Product Selection */}
      <div className="card">
        <h3>Select Products</h3>
        <table>
          <thead>
            <tr>
              <th>Select</th>
              <th>Product</th>
              <th>Price (M)</th>
              <th>Stock</th>
              <th>Quantity</th>
              <th>Subtotal (M)</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => {
              const qty = selectedProducts[product.id] || 0;
              return (
                <tr key={product.id}>
                  <td>
                    <input
                      type="checkbox"
                      checked={!!selectedProducts[product.id]}
                      onChange={() => toggleSelection(product.id)}
                      disabled={product.quantity === 0}
                    />
                  </td>
                  <td>{product.name}</td>
                  <td>M{product.price.toFixed(2)}</td>
                  <td>{product.quantity}</td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      max={product.quantity}
                      value={qty}
                      onChange={(e) =>
                        updateQuantity(product.id, parseInt(e.target.value) || 0)
                      }
                      disabled={!selectedProducts[product.id]}
                      style={{ width: "60px" }}
                    />
                  </td>
                  <td>{qty > 0 ? "M" + (product.price * qty).toFixed(2) : "-"}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Sale Summary */}
      {Object.keys(selectedProducts).length > 0 && (
        <div className="card">
          <h3>Sale Summary</h3>
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Price (M)</th>
                <th>Quantity</th>
                <th>Subtotal (M)</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(selectedProducts)
                .filter(([_, qty]) => qty > 0)
                .map(([id, qty]) => {
                  const product = products.find((p) => p.id === parseInt(id));
                  return (
                    <tr key={id}>
                      <td>{product.name}</td>
                      <td>M{product.price.toFixed(2)}</td>
                      <td>{qty}</td>
                      <td>M{(product.price * qty).toFixed(2)}</td>
                    </tr>
                  );
                })}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="3"><strong>Total</strong></td>
                <td><strong>M{getSubtotal().toFixed(2)}</strong></td>
              </tr>
            </tfoot>
          </table>

          <button className="btn btn-success" onClick={processSale}>Process Sale</button>
          <button
            className="btn btn-secondary"
            style={{ marginLeft: "10px" }}
            onClick={() => setSelectedProducts({})}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default Sales;
