import React, { useState } from "react";

const StockManagement = ({ products, setProducts, transactions, setTransactions }) => {
  const [quantities, setQuantities] = useState({}); // { productId: number }

  const updateQuantity = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: value }));
  };

  const handleStockAdjustment = async (product, action) => {
    const qty = parseInt(quantities[product.id] || 0);
    if (!qty || qty <= 0) {
      alert("Enter a valid quantity.");
      return;
    }

    let newQuantity = product.quantity;
    if (action === "add") newQuantity += qty;
    else if (action === "deduct") newQuantity -= qty;

    if (newQuantity < 0) newQuantity = 0;

    try {
      // Update product
      const res = await fetch(`http://localhost:3001/products/${product.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ quantity: newQuantity }),
      });
      const updatedProduct = await res.json();

      // Record transaction
      const transRes = await fetch("http://localhost:3001/transactions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          productId: product.id,
          type: action === "add" ? "Add Stock" : "Deduct Stock",
          quantity: qty,
          date: new Date().toISOString(),
        }),
      });
      const newTransaction = await transRes.json();

      // Update state
      setProducts((prev) =>
        prev.map((p) => (p.id === updatedProduct.id ? updatedProduct : p))
      );
      setTransactions((prev) => [...prev, newTransaction]);
      setQuantities((prev) => ({ ...prev, [product.id]: "" }));
    } catch (error) {
      console.error("Error adjusting stock:", error);
      alert("Error adjusting stock. Try again.");
    }
  };

  return (
    <div>
      <h2>Stock Management</h2>
      <div className="card">
        <h3>Adjust Stock Levels</h3>
        <table>
          <thead>
            <tr>
              <th>Product</th>
              <th>Stock</th>
              <th>Quantity</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.quantity}</td>
                <td>
                  <input
                    type="number"
                    min="0"
                    value={quantities[product.id] || ""}
                    onChange={(e) => updateQuantity(product.id, e.target.value)}
                  />
                </td>
                <td>
                  <button
                    onClick={() => handleStockAdjustment(product, "add")}
                    className="btn btn-success"
                  >
                    Add Stock
                  </button>
                  <button
                    onClick={() => handleStockAdjustment(product, "deduct")}
                    className="btn btn-danger"
                    style={{ marginLeft: "5px" }}
                  >
                    Deduct Stock
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="card">
        <h3>Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Product</th>
              <th>Type</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t) => {
              const product = products.find((p) => p.id === t.productId);
              return (
                <tr key={t.id}>
                  <td>{new Date(t.date).toLocaleString()}</td>
                  <td>{product ? product.name : "Unknown"}</td>
                  <td>{t.type}</td>
                  <td>{t.quantity}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default StockManagement;
