import React from 'react';

const Reporting = ({ products, transactions }) => {
  // Calculate total inventory value
  const totalInventoryValue = products.reduce(
    (sum, product) => sum + (product.price * product.quantity), 0
  );

  // Calculate low stock items
  const lowStockItems = products.filter(product => product.lowStockAlert);

  // Calculate recent transactions
  const recentTransactions = transactions
    .sort((a, b) => new Date(b.date) - new Date(a.date))
    .slice(0, 10);

  // Calculate sales data for each product
  const productSalesData = products.map(product => {
    const productTransactions = transactions.filter(
      t => t.productId === product.id && t.type === 'sale'
    );
    
    const unitsSold = productTransactions.reduce((sum, t) => sum + t.quantity, 0);
    const totalRevenue = productTransactions.reduce((sum, t) => sum + (t.unitPrice * t.quantity), 0);

    return {
      ...product,
      unitsSold,
      totalRevenue
    };
  });

  // Calculate overall totals
  const totalUnitsSold = productSalesData.reduce((sum, product) => sum + product.unitsSold, 0);
  const totalRevenue = productSalesData.reduce((sum, product) => sum + product.totalRevenue, 0);

  return (
    <div>
      <h2>Reports & Analytics</h2>
      
      <div className="card">
        <h3>Business Summary</h3>
        <div className="summary-grid">
          <div className="summary-item">
            <h4>Total Products</h4>
            <p>{products.length}</p>
          </div>
          <div className="summary-item">
            <h4>Inventory Value</h4>
            <p>M{totalInventoryValue.toFixed(2)}</p>
          </div>
          <div className="summary-item">
            <h4>Units Sold</h4>
            <p>{totalUnitsSold}</p>
          </div>
          <div className="summary-item">
            <h4>Total Revenue</h4>
            <p>M{totalRevenue.toFixed(2)}</p>
          </div>
          <div className="summary-item">
            <h4>Low Stock Items</h4>
            <p>{lowStockItems.length}</p>
          </div>
        </div>
      </div>
      
      <div className="card">
        <h3>Product Sales Performance</h3>
        {productSalesData.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Category</th>
                <th>Current Stock</th>
                <th>Units Sold</th>
                <th>Revenue (M)</th>
              </tr>
            </thead>
            <tbody>
              {productSalesData
                .sort((a, b) => b.unitsSold - a.unitsSold)
                .map(product => (
                  <tr key={product.id}>
                    <td>{product.name}</td>
                    <td>{product.category}</td>
                    <td className={product.lowStockAlert ? 'low-stock-alert' : ''}>
                      {product.quantity}
                    </td>
                    <td>{product.unitsSold}</td>
                    <td>M{product.totalRevenue.toFixed(2)}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        ) : (
          <p>No sales data available.</p>
        )}
      </div>
      
      <div className="card">
        <h3>Low Stock Alerts</h3>
        {lowStockItems.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Current Stock</th>
                <th>Threshold</th>
                <th>Value (M)</th>
              </tr>
            </thead>
            <tbody>
              {lowStockItems.map(product => (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td className="low-stock-alert">{product.quantity}</td>
                  <td>{product.lowStockThreshold}</td>
                  <td>M{(product.quantity * product.price).toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No low stock items at this time.</p>
        )}
      </div>
      
      <div className="card">
        <h3>Recent Transactions</h3>
        {recentTransactions.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Type</th>
                <th>Product</th>
                <th>Quantity</th>
                <th>Amount (M)</th>
              </tr>
            </thead>
            <tbody>
              {recentTransactions.map(transaction => {
                const product = products.find(p => p.id === transaction.productId);
                return (
                  <tr key={transaction.id}>
                    <td>{new Date(transaction.date).toLocaleDateString()}</td>
                    <td>{transaction.type}</td>
                    <td>{product ? product.name : 'Unknown Product'}</td>
                    <td>{transaction.quantity}</td>
                    <td>M{transaction.totalAmount ? transaction.totalAmount.toFixed(2) : (transaction.quantity * (product?.price || 0)).toFixed(2)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : (
          <p>No transactions recorded.</p>
        )}
      </div>
    </div>
  );
};

export default Reporting;
