import React from 'react';

const Reporting = ({ products, transactions }) => {
  // Calculate total inventory value
  const totalInventoryValue = products.reduce(
    (sum, product) => sum + (product.price * product.quantity), 0
  );

  // Calculate low stock items
  const lowStockItems = products.filter(product => product.lowStockAlert);

  // Calculate recent transactions
  const recentTransactions = transactions
    .sort((a, b) => new Date(b.date) - new Date(a.date))
    .slice(0, 10);

  // Calculate sales by product
  const salesByProduct = {};
  transactions
    .filter(t => t.type === 'sale')
    .forEach(transaction => {
      const product = products.find(p => p.id === transaction.productId);
      if (product) {
        if (!salesByProduct[product.name]) {
          salesByProduct[product.name] = 0;
        }
        salesByProduct[product.name] += transaction.quantity;
      }
    });

  return (
    <div>
      <h2>Reports & Analytics</h2>
      
      <div className="card">
        <h3>Inventory Summary</h3>
        <p><strong>Total Products:</strong> {products.length}</p>
        <p><strong>Total Inventory Value:</strong> ${totalInventoryValue.toFixed(2)}</p>
        <p><strong>Low Stock Items:</strong> {lowStockItems.length}</p>
      </div>
      
      <div className="card">
        <h3>Low Stock Alerts</h3>
        {lowStockItems.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Current Stock</th>
                <th>Threshold</th>
              </tr>
            </thead>
            <tbody>
              {lowStockItems.map(product => (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td className="low-stock-alert">{product.quantity}</td>
                  <td>{product.lowStockThreshold}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No low stock items at this time.</p>
        )}
      </div>
      
      <div className="card">
        <h3>Recent Transactions</h3>
        {recentTransactions.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Type</th>
                <th>Product</th>
                <th>Quantity</th>
              </tr>
            </thead>
            <tbody>
              {recentTransactions.map(transaction => {
                const product = products.find(p => p.id === transaction.productId);
                return (
                  <tr key={transaction.id}>
                    <td>{new Date(transaction.date).toLocaleDateString()}</td>
                    <td>{transaction.type}</td>
                    <td>{product ? product.name : 'Unknown Product'}</td>
                    <td>{transaction.quantity}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : (
          <p>No transactions recorded.</p>
        )}
      </div>
      
      <div className="card">
        <h3>Sales by Product</h3>
        {Object.keys(salesByProduct).length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Units Sold</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(salesByProduct).map(([product, quantity]) => (
                <tr key={product}>
                  <td>{product}</td>
                  <td>{quantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No sales data available.</p>
        )}
      </div>
    </div>
  );
};

export default Reporting;
