import coffeeImg from "./coffee.jpg";
import sandwichImg from "./sandwich.jpg";
import niknaksImg from "./niknaks.jpg";
import breadImg from "./bread.jpg";


import React from "react";

const Dashboard = ({ products }) => {
  const lowStockProducts = products.filter((product) => product.lowStockAlert);
  const totalProducts = products.length;
  const totalValue = products.reduce(
    (sum, product) => sum + product.price * product.quantity,
    0
  );

  return (
    <div className="dashboard">
      {/* === Stats Section (Top Row) === */}
      <div className="stats-row">
        <div className="card">
          <h3>Total Products</h3>
          <p>{totalProducts}</p>
        </div>

        <div className="card">
          <h3>Inventory Value</h3>
          <p>M{totalValue.toFixed(2)}</p>
        </div>

        <div className="card">
          <h3>Low Stock Alerts</h3>
          <p>{lowStockProducts.length} products need restocking</p>
        </div>

        <div className="card">
          <h3>Low Stock Items</h3>
          <ul className="low-stock-list">
            {lowStockProducts.map((product) => (
              <li key={product.id} className="low-stock-item">
                <span>{product.name}</span>
                <span className="low-stock-alert">{product.quantity} left</span>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* === Product Cards Section (Below Stats) === */}
      <div className="product-gallery">
        {products.slice(0, 4).map((product) => (
          <div key={product.id} className="product-card">
            <img
              src={product.imageUrl}
              alt={product.name}
              className="product-image"
            />
            <div className="product-info">
              <h4>{product.name}</h4>
              <p>M{product.price.toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
