import React, { useState } from 'react';

const StockManagement = ({ products, transactions, refreshProducts, refreshTransactions }) => {
  const [selectedProduct, setSelectedProduct] = useState('');
  const [adjustmentType, setAdjustmentType] = useState('add');
  const [quantity, setQuantity] = useState('');

  const handleStockAdjustment = async (e) => {
    e.preventDefault();
    
    if (!selectedProduct || !quantity) return;
    
    const product = products.find(p => p.id === parseInt(selectedProduct));
    if (!product) return;
    
    const adjustmentQuantity = parseInt(quantity);
    let newQuantity = product.quantity;
    
    if (adjustmentType === 'add') {
      newQuantity += adjustmentQuantity;
    } else if (adjustmentType === 'remove') {
      newQuantity -= adjustmentQuantity;
      if (newQuantity < 0) newQuantity = 0;
    }
    
    try {
      // Update product quantity
      await fetch(`http://localhost:3001/products/${product.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      
      // Record transaction
      await fetch('http://localhost:3001/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          productId: product.id,
          type: adjustmentType === 'add' ? 'restock' : 'adjustment',
          quantity: adjustmentQuantity,
          date: new Date().toISOString()
        })
      });
      
      setSelectedProduct('');
      setQuantity('');
      refreshProducts();
      refreshTransactions();
    } catch (error) {
      console.error('Error adjusting stock:', error);
    }
  };

  return (
    <div>
      <h2>Stock Management</h2>
      
      <div className="card">
        <h3>Adjust Stock Levels</h3>
        <form onSubmit={handleStockAdjustment}>
          <div className="form-group">
            <label>Product</label>
            <select 
              value={selectedProduct} 
              onChange={(e) => setSelectedProduct(e.target.value)}
              required
            >
              <option value="">Select a product</option>
              {products.map(product => (
                <option key={product.id} value={product.id}>
                  {product.name}
                </option>
              ))}
            </select>
          </div>
          
          <div className="form-group">
            <label>Adjustment Type</label>
            <select 
              value={adjustmentType} 
              onChange={(e) => setAdjustmentType(e.target.value)}
              required
            >
              <option value="add">Add Stock</option>
              <option value="remove">Remove Stock</option>
            </select>
          </div>
          
          <div className="form-group">
            <label>Quantity</label>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
              min="1"
            />
          </div>
          
          <button type="submit" className="btn btn-primary">
            Adjust Stock
          </button>
        </form>
      </div>
      
      <div className="card">
        <h3>Stock Transactions</h3>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Product</th>
              <th>Type</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(transaction => {
              const product = products.find(p => p.id === transaction.productId);
              return (
                <tr key={transaction.id}>
                  <td>{new Date(transaction.date).toLocaleDateString()}</td>
                  <td>{product ? product.name : 'Unknown Product'}</td>
                  <td>{transaction.type}</td>
                  <td>{transaction.quantity}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default StockManagement;
