import React from 'react';

const Dashboard = ({ products, transactions }) => {
  const lowStockProducts = products.filter(product => product.lowStockAlert);
  const totalProducts = products.length;
  const totalValue = products.reduce((sum, product) => sum + (product.price * product.quantity), 0);
  
  // Calculate sales and profit data for each product
  const productsWithSalesData = products.map(product => {
    // Get all sales transactions for this product
    const productSales = transactions.filter(
      transaction => transaction.productId === product.id && transaction.type === 'sale'
    );
    
    // Calculate total units sold
    const unitsSold = productSales.reduce((sum, transaction) => sum + transaction.quantity, 0);
    
    // Calculate total revenue (assuming selling price is the same as product price)
    const totalRevenue = unitsSold * product.price;
    
    // For profit calculation, we need cost price - for now using 70% of selling price as estimate
    // In a real system, you would have a costPrice field in your product data
    const costPrice = product.price * 0.7; // Assuming 30% profit margin
    const totalCost = unitsSold * costPrice;
    const totalProfit = totalRevenue - totalCost;
    
    return {
      ...product,
      unitsSold,
      totalRevenue,
      totalProfit
    };
  });

  // Calculate overall totals
  const totalRevenue = productsWithSalesData.reduce((sum, product) => sum + product.totalRevenue, 0);
  const totalProfit = productsWithSalesData.reduce((sum, product) => sum + product.totalProfit, 0);
  const totalUnitsSold = productsWithSalesData.reduce((sum, product) => sum + product.unitsSold, 0);

  return (
    <div>
      <h2>Dashboard Overview</h2>
      
      <div className="dashboard">
        <div className="card">
          <h3>Total Products</h3>
          <p>{totalProducts}</p>
        </div>
        
        <div className="card">
          <h3>Inventory Value</h3>
          <p>M{totalValue.toFixed(2)}</p>
        </div>
        
        <div className="card">
          <h3>Total Revenue</h3>
          <p>M{totalRevenue.toFixed(2)}</p>
        </div>
        
        <div className="card">
          <h3>Total Profit</h3>
          <p>M{totalProfit.toFixed(2)}</p>
        </div>
        
        <div className="card">
          <h3>Units Sold</h3>
          <p>{totalUnitsSold}</p>
        </div>
        
        <div className="card">
          <h3>Low Stock Alerts</h3>
          <p>{lowStockProducts.length} products need restocking</p>
        </div>
      </div>

      <div className="card">
        <h3>Product Performance</h3>
        <div className="product-cards">
          {productsWithSalesData.map(product => (
            <div key={product.id} className="product-card">
              <h4>{product.name}</h4>
              <div className="product-details">
                <p><strong>Category:</strong> {product.category}</p>
                <p><strong>Price:</strong> M{product.price.toFixed(2)}</p>
                <p><strong>In Stock:</strong> {product.quantity}</p>
                <p><strong>Units Sold:</strong> {product.unitsSold}</p>
                <p><strong>Revenue:</strong> M{product.totalRevenue.toFixed(2)}</p>
                <p><strong>Profit:</strong> M{product.totalProfit.toFixed(2)}</p>
                <p><strong>Status:</strong> 
                  <span className={product.lowStockAlert ? 'low-stock-alert' : 'in-stock'}>
                    {product.lowStockAlert ? 'Low Stock' : 'In Stock'}
                  </span>
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
      
      <div className="card">
        <h3>Low Stock Items Needing Attention</h3>
        {lowStockProducts.length > 0 ? (
          <ul className="low-stock-list">
            {lowStockProducts.map(product => (
              <li key={product.id} className="low-stock-item">
                <span>{product.name}</span>
                <span className="low-stock-alert">{product.quantity} left (Threshold: {product.lowStockThreshold})</span>
              </li>
            ))}
          </ul>
        ) : (
          <p>No low stock items at this time. Great job!</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
